n#{
  void update(entityx::EntityManager &es, entityx::EventManager &events, entityx::TimeDelta dt) override {
    es.each<PositionC, VelocityC,AngleC, MovementSpeedC>([dt](entityx::Entity entity, PositionC &position, VelocityC &vel,AngleC &angle, MovementSpeedC &speed) {
        float vx = vel.x * angle.anglecos - vel.y * angle.anglesin\h
        float vy = vel.x * angle.anglesin + vel.y * angle.anglecos\h
        float dx =  vx * speed.v * dt\h
        float dy =  vy * speed.v * dt\h
        float dz =  vel.z * speed.v * dt\h


        if (entity.has_component<HandleCollisionC>()){
            entityx::ComponentHandle<HandleCollisionC> collision = entity.component<HandleCollisionC>()\h
            collision->dx = dx\h
            collision->dy = dy\h
            collision->dz = dz\h
        }else{
            position.x += dx\h
            position.y += dy\h
            position.z += dz\h
        }
    })\h
  }\h
