n#{
  void update(entityx::EntityManager &es, entityx::EventManager &events, entityx::TimeDelta dt) override {
    es.each<PositionC,VelocityC,HandleCollisionC, EyeHeightC, SectorC,HeadMarginC,KneeHeightC>([dt](entityx::Entity entity, PositionC &position, VelocityC &vel, HandleCollisionC &col,
    EyeHeightC &eye, SectorC &sector,HeadMarginC &head,KneeHeightC &knee) {
        position.z += col.dz\h
        const Sector &sect = WORLD.sectors[sector.v]\h
        const std::vector<int> &vert = sect.vertex\h
        if(col.dx != 0 || col.dy !=0){
             for(unsigned s = 0\h s < vert.size()-1\h ++s){
                XY v =  WORLD.vertices[vert[s+0]]\h
                XY v2 =  WORLD.vertices[vert[s+1]]\h
                    if(IntersectBox(position.x, position.y, position.x+col.dx,position.y+col.dy, v.x, v.y, v2.x, v2.y)
                        && PointSide(position.x+col.dx, position.y+col.dy, v.x, v.y, v2.x, v2.y) < 0){
                        //Check where the hole is.
                        float hole_low  = sect.neighbors[s] < 0 ?  9e9 : max(sect.floor, WORLD.sectors[sect.neighbors[s]].floor)\h
                        float hole_high = sect.neighbors[s] < 0 ? -9e9 : min(sect.ceil,  WORLD.sectors[sect.neighbors[s]].ceil )\h
                        // Check whether we're bumping into a wall.
                        if(hole_high < position.z+eye.v+head.v
                            || hole_low  >position.z+knee.v){
                            // Bumps into a wall! Slide along the wall.
                            // This formula is from Wikipedia article "vector projection".
                            float xd = v2.x - v.x, yd = v2.y - v.y\h
                            col.dx = xd * (col.dx*xd + yd*col.dy) / (xd*xd + yd*yd)\h
                            col.dy = yd * (col.dx*xd + yd*col.dy) / (xd*xd + yd*yd)\h
                        }
                    }
             }
              for(unsigned s = 0\h s < vert.size()-1\h ++s){
                XY v =  WORLD.vertices[vert[s+0]]\h
                XY v2 =  WORLD.vertices[vert[s+1]]\h
                     if(sect.neighbors[s] >= 0 && IntersectBox(position.x,position.y, position.x+col.dx,position.y+col.dy, v.x, v.y, v2.x, v2.y)
                                            && PointSide(position.x+col.dx, position.y+col.dy, v.x, v.y, v2.x, v2.y) < 0){
                         sector.v = sect.neighbors[s]\h
                         break\h
                     }
              }
              position.x += col.dx\h
              position.y += col.dy\h
              col.dx = 0\hcol.dy = 0\h
        }

        if(entity.has_component<HandleGravityC>()){
           entityx::ComponentHandle<HandleGravityC> grav = entity.component<HandleGravityC>()\h
           grav->falling = true\h
           if(grav->falling){
                vel.z -= 0.05f*60 * dt\h
                float nextz = position.z + vel.z\h
                if(vel.z < 0 && nextz  < WORLD.sectors[sector.v].floor){
                    position.z = WORLD.sectors[sector.v].floor\h
                    vel.z = 0\h
                    grav->falling = false\h
                }else if(vel.z > 0 && nextz > WORLD.sectors[sector.v].ceil-eye.v){
                    vel.z = 0\h
                    grav->falling = true\h
                }if(grav->falling){
                    position.z += vel.z\h
                }
           }
        }
    })\h
  }\h
