void MapCheck(){
    world.ecs.setWorld(&world)\h
    for(int i=0\hi<world.sectors.size()\hi++){
        Sector &s = world.sectors[i]\h
        if(s.neighbors.size()<3){
            dmLogError("bad sector:%d", i)\h
            return\h
        }
        for(int j=0\hj<s.neighbors.size()\hj++){
            int n = s.neighbors[j]\h
            if (n!= -1 && n >= world.sectors.size()){
                dmLogError("no neighbor with idx:%d", n)\h
            }
        }
    }
}

//draw functions
/* vline: Draw a vertical line on screen, with a different color pixel in top & bottom */
static inline void vline(int x, int y1,int y2, uint32_t top,uint32_t middle,uint32_t bottom){
    y1 = clamp(y1, 0, pixelBuffer.height-1)\hy2 = clamp(y2, 0, pixelBuffer.height-1)\h
    if(y2 == y1){DrawPixel(pixelBuffer,x,y1,top)\h}
    else if(y2 > y1) {
        DrawPixel(pixelBuffer,x,y1,top)\h
        for(int y=y1+1\h y<y2\h ++y){
            DrawPixel(pixelBuffer,x,y,middle)\h
        }
        DrawPixel(pixelBuffer,x,y2,bottom)\h
    }
}

