void DrawScreen(entityx::Entity e){
    entityx::ComponentHandle<PositionC> posC = e.component<PositionC>()\h
    entityx::ComponentHandle<AngleC> angleC = e.component<AngleC>()\h
    entityx::ComponentHandle<YawC> yawC = e.component<YawC>()\h
    entityx::ComponentHandle<SectorC> sectorC = e.component<SectorC>()\h
   // MovePlayer(world.player.where.x, world.player.where.y)\h
    //clearBuffer1(&pixelBuffer)\h
    const int W = pixelBuffer.width\h
    const int H =  pixelBuffer.height\h

    enum {MaxQueue  =32}\h
    struct item {int sectorno,sx1,sx2\h} queue[MaxQueue ], *head = queue, *tail=queue\h
    std::vector<int> ytop(W)\h // keep track if remaining windom(min,max) in each column
    std::vector<int> ybottom(W, H-1)\h
    std::vector<int> renderedsectors(world.sectors.size())\h
    //start rendering from player sector
    *head = (struct item) {sectorC->v,0, W-1}\h
    if (++head == queue + MaxQueue ) head = queue\h
    do{
        //pick a sector then slice from the queue to draw
        const struct item now = *tail\h
        if(++tail == queue + MaxQueue) tail = queue\h

        if(renderedsectors[now.sectorno] & 0x21) continue\h // Odd = still rendering, 0x20 = give up try 32 times
        ++renderedsectors[now.sectorno]\h
        //const for pointer and const for data
        const Sector &sect = world.sectors[now.sectorno]\h
        //render each wall of player sector that is facing towards player

        for(unsigned s =0\h s< sect.vertex.size()-1\hs++){
            //acquire the x,y coordinates of the two endpoints(vertices) of thius edge of the sector
            //transform the vertices into the player view
            XY v1 = world.vertices[sect.vertex[s]], v2 = world.vertices[sect.vertex[s+1]]\h
            float vx1 = v1.x - posC->x, vy1 = v1.y- posC->y\h
            float vx2 = v2.x - posC->x, vy2 = v2.y- posC->y\h
            //rotate them around player
            float pcos = angleC->anglecos, psin = angleC->anglesin\h
            float tx1 = vx1 * psin - vy1 * pcos, tz1 = vx1 * pcos +vy1 * psin\h
            float tx2 = vx2 * psin - vy2 * pcos, tz2 = vx2 * pcos +vy2 * psin\h
            //is the wall in front of player
            if(tz1<=0 && tz2 <=0) continue\h
               //if it partialy behind the player, clip it against player's view frustrum
            if(tz1 <= 0 || tz2<=0){
                float nearz = 1.0e-4, farz = 5, nearside = 1.0e-5, farside = 20\h
                //find an intersection between the wall and approximate edges if player's view
                XY i1 = Intersect(tx1,tz1,tx2,tz2, - nearside, nearz, -farside, farz)\h
                XY i2 = Intersect(tx1,tz1,tx2,tz2, nearside, nearz, farside, farz)\h
                if(tz1 < nearz){
                    if(i1.y > 0){tx1 = i1.x\htz1 = i1.y\h}
                    else{tx1 = i2.x\htz1 = i2.y\h}
                }
                if(tz2 < nearz){
                    if(i1.y > 0){tx2 = i1.x\htz2 = i1.y\h}
                    else{tx2 = i2.x\htz2 = i2.y\h}
                }
            }
            //Perspective transformation
            float xscale1 = hfov / tz1, yscale1 = vfov/tz1\h int x1 = W/2 - (int)(tx1 * xscale1)\h
            float xscale2 = hfov / tz2, yscale2 = vfov/tz2\h int x2 = W/2 - (int)(tx2 * xscale2)\h
            //only render if visible
            //if right vertices is left to screen. Or lefy is right. That mean that edge not visible
            //x1 >= x2 wtf
            if(x1 >= x2 || x2 < now.sx1 || x1 > now.sx2) continue\h
            //acquire the floor and ceilings height, relative to player view\h
            float yceil = sect.ceil - posC->z\h
            float yfloor = sect.floor - posC->z\h


            //check the edge type, neighor=-1 means wall, other boundary between two sectors.
            int neighbor = sect.neighbors[s]\h

            float nyceil = 0, nyfloor = 0\h
            if (neighbor>=0){
                nyceil =  world.sectors[neighbor].ceil - posC->z\h
                nyfloor = world.sectors[neighbor].floor - posC->z\h
            }
            \sdefine Yaw(y,z) (y + z)//z*yawC->yaw)
            //project floor/ceiling height into screen coordinates(Y)
              int y1a  = H/2 - (int)(Yaw(yceil, tz1) * yscale1),  y1b = H/2 - (int)(Yaw(yfloor, tz1) * yscale1)\h
              int y2a  = H/2 - (int)(Yaw(yceil, tz2) * yscale2),  y2b = H/2 - (int)(Yaw(yfloor, tz2) * yscale2)\h
              /* The same for the neighboring sector */
              int ny1a = H/2 - (int)(Yaw(nyceil, tz1) * yscale1), ny1b = H/2 - (int)(Yaw(nyfloor, tz1) * yscale1)\h
              int ny2a = H/2 - (int)(Yaw(nyceil, tz2) * yscale2), ny2b = H/2 - (int)(Yaw(nyfloor, tz2) * yscale2)\h
            /*Render the wall */
            int beginx = max(x1, now.sx1), endx = min(x2, now.sx2)\h
            for(int x = beginx\h x<=endx\h ++x){
                /* Calculate the Z coordinate for this point. (Only used for lighting.) */
                int z = ((x - x1) * (tz2-tz1) / (x2-x1) + tz1) * 8\h
                /* Acquire the Y coordinates for our ceiling & floor for this X coordinate. Clamp them. */
                int ya = (x - x1) * (y2a-y1a) / (x2-x1) + y1a, cya = clamp(ya, ytop[x],ybottom[x])\h // top
                int yb = (x - x1) * (y2b-y1b) / (x2-x1) + y1b, cyb = clamp(yb, ytop[x],ybottom[x])\h // bottom
                //render ceiling\heverything above this sector's ceiling height
                vline(x,ytop[x],cya-1,0x11111100, 0x22222200, 0x11111100)\h
                //render floor\heverything below this sector's floor height
                 vline(x,cyb+1,ybottom[x],0x0000FF00, 0x0000AA00, 0x0000FF00)\h

                 if(neighbor >=0){
                    //same for their floor and ceiling
                     int nya = (x-x1) * (ny2a-ny1a)/ (x2-x1) +ny1a, cnya = clamp(nya, ytop[x], ybottom[x])\h//top
                     int nyb = (x-x1) * (ny2b-ny1b)/ (x2-x1) +ny1b, cnyb = clamp(nyb, ytop[x], ybottom[x])\h//bottom
                     //if our ceiling in higher than their ceiling, render upper wall
                    int r1 = 0x01010100 * (255-z), r2 = 0x04000700 * (31-z/8)\h
                    vline(x,cya,cnya-1,0,x==x1 || x==x2 ? 0: r1,0)\h //between our and their ceiling
                      ytop[x] = clamp(max(cya,cnya),ytop[x], H-1)\h

                      //if our floor is lower than their floor, render bottom wall
                       vline(x,cnyb+1,cyb,0,x==x1 || x==x2 ? 0: r2,0)\h //between their and our floor
                       ybottom[x] = clamp(min(cyb,cnyb),0,ybottom[x])\h

                 }else{
                    //draw wall
                    unsigned r = 0x01010100 * (255-z)\h
                    vline(x,cya,cyb,0,x==x1 || x==x2 ? 0 : r, 0)\h
                 }
            }
            //shedule the neighbors rendering with it window
            if (neighbor >=0 && endx >= beginx && (head + MaxQueue + 1 - tail)%MaxQueue){
                *head = (struct item){neighbor,beginx, endx}\h
                if(++head == queue + MaxQueue) head = queue\h
            }
        }
    ++renderedsectors[now.sectorno]\h
    }while(head!= tail)\h
}#0

